<?phpheader("Content-type: text/xml"); require_once('../app/Mage.php');Mage::App('default');	$xml_output = "<?xml version=\"1.0\"?>\n";	$xml_output .= "<!DOCTYPE plist PUBLIC \"-//Apple Computer//DTD PLIST 1.0//EN\"\"http://www.apple.com/DTDs/PropertyList-1.0.dtd\">";	$xml_output .= "<plist version=\"1.0\">\n";	$xml_output .= "<array>\n";	$myShippingModel = Mage::getSingleton('Sushi_Holiday_Model_Holiday');	$val = $myShippingModel->deliverydate();			$curDate =  date("Gi",Mage::getModel('core/date')->timestamp(time())); 	for($i=9;$i<22;$i++) 	{		$delivery_time[$i.':00']=date("D j M ").$i.':00';		$delivery_time[$i.':20']=date("D j M ").$i.':20';		$delivery_time[$i.':40']=date("D j M ").$i.':40';	}																				if($val == 0) {						if($curDate > 900 && $curDate < 2200)						{							foreach($delivery_time as $key => $val)							{								$time = explode(":",$key);								$time = implode("",$time);								$time = (int)$time;								if($time > $curDate)								{										$xml_output .= "<dict>\n";									$xml_output .=	"<key>Time</key>\n";									$xml_output .=	"<string>".$key."</string>\n";									$xml_output .=	"</dict>";                                								}							}							}						else						{									$xml_output .= "<dict>\n";									$xml_output .=	"<key>Time</key>\n";									$xml_output .=	"<string>Closed</string>\n";									$xml_output .=	"</dict>";																				}					}					else 					{																	$xml_output .= "<dict>\n";									$xml_output .=	"<key>Time</key>\n";									$xml_output .=	"<string>Holiday</string>\n";									$xml_output .=	"</dict>";					}				$xml_output .= "</array>\n";				$xml_output .= "</plist>\n";				echo $xml_output;		?>	